import React, {useState} from 'react';
import $ from "jquery"

const ClientSearch= () => {

    const [tempName, setTempName] = useState("");

    const [customer_name, setCustomerName] = useState("");
    const [phone, setPhone] = useState("");
    const [addr, setAddr] = useState("");

    const submitCustomer = async (customer_name, phone, addr) => {
        try {
            const body = {customer_name, phone, addr};

            const options = {
                method: 'POST',
                headers: {'Content-Type': 'application/json'},
                body: JSON.stringify(body)
            }
            const response = await fetch('http://localhost:3001/customers/api/v1/cust/spec', options);



            // Fast solution for table refresh
            //window.location.reload();

        } catch (error) {
            console.log(error.message);
        }
    }

    /*
    // method onSubmitForm has a POST request
    const onSubmitForm = async (e) => {
        e.preventDefault();

        try {
            const body = {customer_id, date};

            const options = {
                method: 'POST',
                headers: {'Content-Type': 'application/json'},
                body: JSON.stringify(body)
            }
            const response = await fetch('http://localhost:3001/orders/api/v1/ord/', options)
            console.log(response);


            // Fast solution for table refresh
            //window.location.reload();

        } catch (error) {
            console.log(error.message);
        }

    }
    */

    // Efeitos Jquery: informa se busca retornou cliente ou nao

    $("#success-btn").click(function() {
        $( "div.success" ).fadeIn( 300 ).delay( 2500 ).fadeOut( 400 );
      });

      $("#failure-btn").click(function() {
        $( "div.failure" ).fadeIn( 300 ).delay( 2500 ).fadeOut( 400 );
      });




    return (
        <>
        <div className="container">
            <h5>Customer Search</h5>

            <button type="button" className="btn btn-secondary" data-toggle="modal" data-target="#myModal">
                Search
            </button>

            <div className="modal" id="myModal">
                <div className="modal-dialog modal-dialog-scrollable">
                <div className="modal-content">

                    <div className="modal-header">
                    <h5 className="modal-title">Customer Search</h5>
                    <button type="button" className="close" data-dismiss="modal">Ã—</button>
                    </div>

                    <div className="modal-body">
                        <div className="input-group col-md-4 m-1">
                            <input className="form-control py-2 border-right-0 border" onChange={e=>setTempName(e.target.value)} type="search" placeholder="search" id={"example-search-input"}/>
                            <span className="input-group-append">
                                    <button className="btn btn-outline-secondary border-left-0 border" type="button" onClick={() =>{}}>
                                        <i className="fa fa-search"></i>
                                    </button>
                            </span>
                        </div> 
                        <table className="table">
                                <thead>
                                <tr>
                                    <th>Customer Name</th>
                                    <th>Phone</th>
                                    <th>Address</th>
                                </tr>
                                </thead>
                                {customer_name && customer_name.map((cust) => {
                                    return (
                                        <tbody>                                                                                      
                                        <tr key = {cust.id}>
                                            <td>{cust.prod_name}</td>
                                            <td>{cust.phone}</td>
                                            <td>{cust.addr}</td>
                                        </tr>
                                        </tbody>                 
                                    );
                                })}                                         
                            </table>
                        <p>
                            <button id="success-btn">Success</button>
                            <button id="failure-btn">Failure</button>
                        </p>
                        <div className="alert-box success">Customer found in database !!!</div>
                        <div className="alert-box failure">Customer not found in database !!!</div>


                    <label style={{color: "black"}}>Customer Name:</label>
                    <input type="text" className="form-control" value={customer_name} placeholder="Name" onChange={e => setCustomerName(e.target.value)}/>

                    <label style={{color: "black"}}>Customer Phone</label>
                    <input type="text" className="form-control" value={phone} placeholder="Phone (11-Digit)" onChange={e => setPhone(e.target.value)}/>

                    <label style={{color: "black"}}>Address:</label>
                    <input type="text" className="form-control" value={addr} placeholder="Address" onChange={e => setAddr(e.target.value)}/>
                    </div>


                    <div className="modal-footer">
                    <button type="button" className="btn btn-success" data-dismiss="modal" onClick={()=>submitCustomer()}>Add</button>
                    <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
                    </div>

                </div>
                </div>
            </div>

        </div>


        </>
    );
};

export default ClientSearch;
